<script lang="ts">
	import { name, street, plz, city, phone, mail } from './info';
	function genVCard(): string {
		let vCard = '';
		vCard += 'BEGIN:VCARD\n';
		vCard += 'VERSION:3.0\n';
		vCard += 'PRODID:-//Apple Inc.//iPhone OS 16.6//EN\n';
		vCard += `ORG:${name};\n`;
		vCard += `TEL;TYPE=WORK;type=VOICE:+49${phone().substring(1)};\n`;
		vCard += `EMAIL;TYPE=INTERNET:${mail()}\n`;
		vCard += `item1.ADR;TYPE=WORK:;;${street};${city};;${plz}; Deutschland\n`;
		vCard += `item1.X-ABADR:de;\n`;
		vCard += `URL;TYPE=WORK:https://it-rahn.de\n`;
		vCard +=
			'PHOTO;ENCODING=b;TYPE=JPEG:\n';
		vCard += 'END:VCARD';

		return encodeURIComponent(vCard);
	}

	import { browser } from '$app/environment';

	let href = '';

	if (browser) {
		href = 'data:text/vcard;charset=UTF-8,' + genVCard();
	}

	let classes = '';
	export { classes as class };
</script>

<a class={classes} download="Rahn-IT.vcf" {href}>
	<slot />
</a>
